import YouTubeIframeLoader from 'youtube-iframe';
import $ from 'jquery';
import 'slick-carousel';

let stateFlags = {
  playerReady: false,
  playing: false,
  player: false,
  canAuto: false,
  playinline: 1
}

let sliderSettings = {
  dots: true,
  arrows: false,
  autoplay: true,
  autoplaySpeed: 7000
}

function detect_autoplay() {
  let auto_content = document.createElement('video');

  let mp4 = document.createElement('source');
  mp4.src = "data:video/mp4;base64,";

  let webm = document.createElement('source');
  webm.src = "data:video/webm;base64,";

  auto_content.appendChild(webm);
  auto_content.appendChild(mp4);

  auto_content.id = "base64_test_video";
  auto_content.autoplay = true;
  auto_content.muted = true;
  auto_content.style.position = "fixed";
  auto_content.style.left = "5000px";

  document.getElementsByTagName("body")[0].appendChild(auto_content);
  
  let base64_test_video = document.getElementById("base64_test_video");
  

  setTimeout(() => {
    let promise = base64_test_video.play()

    if(promise !== undefined) {
      promise.then(_ => {

      }).catch(error => {
        console.log(error);
      });
    }

    if(!base64_test_video.paused) {
      stateFlags.canAuto = true;
    }
    
    
  }, 100);
}

function _onStateChange(e) {
  
  switch (e.data) {
    case 0: //ended
      _onEnd(e);
      break;
    case 1: //playing
      document.getElementById('banner').removeAttribute('style');
      break;
    case 5: //video cued
      
      break;
  }
}

function _onReady(e) {

  if(stateFlags.playinline === 0) {
    e.target.unMute();
  } else {
    e.target.mute();
  }

  stateFlags.playerReady = true;
  stateFlags.playing = false;
  stateFlags.player = e.target;

  
}

function _onEnd(e) {
  e.target.playVideo();
}

function _onPlay(e) {
  stateFlags.playing = true;
  
}

function _onPause(e) {
  if(stateFlags.playinline === 0) {
    stateFlags.playinline = 1;
  }
}

function fullScreenExit() {
  if(stateFlags.player.isMuted()) {
    stateFlags.player.unMute();
  } else {
    stateFlags.player.mute();
  }
}

function onPlayClick() {
  if(stateFlags.player) {
    let iFrame = document.getElementById('video-iframe');
    let requestFullScreen = iFrame.requestFullScreen || iFrame.mozRequestFullScreen || iFrame.webkitRequestFullScreen;

    if(requestFullScreen) {
      requestFullScreen.bind(iFrame)();
    } else {
      stateFlags.playinline = 0;
    }
  }
}

export default {
  init() {
    document.addEventListener('webkitfullscreenchange', fullScreenExit, false);
    document.addEventListener('fullscreenchange', fullScreenExit, false);
    document.addEventListener('mozfullscreenchange', fullScreenExit, false);
    document.addEventListener('MSFullscreenChange', fullScreenExit, false);
    document.getElementById('toggle-full-video').addEventListener('click', onPlayClick);

    YouTubeIframeLoader.load(function(YT) {
      new YT.Player('video-iframe', {
        videoId: window.siteData.video,
        events: {
          'onReady': _onReady,
          'onStateChange': _onStateChange
        },
        playerVars: {
          autoplay: 1,
          controls: 0,
          rel: 0,
          showinfo: 0,
          playsinline: stateFlags.playinline
        }
      });
    });

    $('.slick-slider').slick(sliderSettings);
  },
  finalize() {
    
  },
};
